         ('D', 'F'): 4, ('E', 'F'): 9, ('E', 'G'): 3, ('F', 'G'): 5, ('G', 'H'): 3}

for edge, weight in edges.items():
    u, v = edge
    graph[u][v] = weight
    graph[v][u] = weight

degrees = {node: sum(graph[node].values()) for node in graph}
odd_degree_nodes = [node for node in graph if degrees[node] % 2 != 0]
def shortest_path(graph, start, end):
    shortest_paths = {node: float('inf') for node in graph}
    shortest_paths[start] = 0

    current = start
    while current != end:
        for neighbor, weight in graph[current].items():
            new_distance = shortest_paths[current] + weight
            if new_distance < shortest_paths[neighbor]:
                shortest_paths[neighbor] = new_distance

        current = min(shortest_paths, key=shortest_paths.get)

    return shortest_paths[end]

