def tax_revenue(t):
    consumption = max(53 - 100 * (t / 100), 0)
    return consumption * 0.15 * t

def maximize_tax_revenue():
    max_revenue = 0
    max_tax_rate = 0
    for t in range(0, 101):
        revenue = tax_revenue(t)
        if revenue > max_revenue:
            max_revenue = revenue
            max_tax_rate = t
    return max_tax_rate

solution = maximize_tax_revenue()
